"use strict";var Vibrant=require("node-vibrant"),getRandom=function(t,r){return r?Math.floor(Math.random()*(t-r)+r):Math.floor(Math.random()*t)},convertColor=function(t){var r=t.substr(1);return parseInt(r,16)},getColorOfImage=function(t){new Vibrant(t).getPalette().then(function(t){var r=t.Vibrant.getHex();return parseInt(r.substr(1),16)})};module.exports={getRandom:getRandom,convertColor:convertColor,getColorOfImage:getColorOfImage};